create table tb_aluguel (id bigint generated by default as identity, data_inicio timestamp, data_termino timestamp, valor_do_contrato DOUBLE PRECISION, cliente_id bigint not null, endereco_id bigint, equipamento_id bigint not null, primary key (id));
create table tb_cliente (id bigint generated by default as identity, cpf_cnpj varchar(255), nome varchar(255), telefone1 varchar(255), telefone2 varchar(255), endereco_id bigint, primary key (id));
create table tb_endereco (id bigint generated by default as identity, bairro varchar(255), cep varchar(255), localidade varchar(255), logradouro varchar(255), numero varchar(255), uf varchar(255), primary key (id));
create table tb_equipamento (id bigint generated by default as identity, nome varchar(255), valor_aluguel_dia DOUBLE PRECISION, valor_aluguel_mes DOUBLE PRECISION, valor_aluguel_quinzena DOUBLE PRECISION, valor_da_compra DOUBLE PRECISION, primary key (id));
create table tb_role (id bigint generated by default as identity, authority varchar(255), primary key (id));
create table tb_user (id bigint generated by default as identity, email varchar(255), first_name varchar(255), last_name varchar(255), password varchar(255), primary key (id));
create table tb_user_role (user_id bigint not null, role_id bigint not null, primary key (user_id, role_id));
